#define T iTime
#define R iResolution

void mainImage( out vec4 fragColor, in vec2 coord ) {
    vec2 uv = coord/R.xy;
    vec2 uvc = (coord-R.xy/2.)/R.y;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uvc.xyx+vec3(1,1,1));
    vec3 col = vec3(0.5, 0.5, 0.5);
    vec2 vr = vec2(0.25*sin(T), 0.25*cos(T));
    if (dot(uvc, uvc) >= 0.25 && vr.x * uvc.y - vr.y * uvc.x < .0)
        col = vec3(1., 1., 1.);
    if (dot(uvc, uvc) >= 0.25 && vr.x * uvc.y - vr.y * uvc.x >= .0)
        col = vec3(.0, .0, .0);
    if (dot(uvc, uvc) < 0.25 && vr.x * uvc.y - vr.y * uvc.x >= .0)
        col = vec3(1., 1., 1.);
    if (dot(uvc, uvc) < 0.25 && vr.x * uvc.y - vr.y * uvc.x < .0)
        col = vec3(.0, .0, .0);
    if (dot(uvc - vr, uvc - vr) < 0.0625)
        col = vec3(1., 1., 1.);
    if (dot(uvc + vr, uvc + vr) < 0.0625)
        col = vec3(.0, .0, .0);
    if (dot(uvc - vr, uvc - vr) < 0.006)
        col = vec3(.0, .0, .0);
    if (dot(uvc + vr, uvc + vr) < 0.006)
        col = vec3(1., 1., 1.);
    // Output to screen
    fragColor = vec4(col,1.0);
}

