void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float mousePercent = iMouse.x / iResolution.x;

    float trigger = (1.0 + sign(mousePercent - 0.5)) / 2.0;
    float instructionCount = trigger * pow(2.0, 23.0);

    vec4 color = vec4(0.0);
    for (float i = 0.0; i < instructionCount; i += 1.0 / pow(2.0, 10.0)) {
        color.r += i * sin(cos(tan(pow(2.0, 99.0))));
    }

    fragColor = color;
}
