void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 center = vec2(0.5, 0.5);
    float strength = 0.5f;
    float spread = 0.5f;
    float width = .2f;
    float inner_dist = .2f;
    float noise_str = .025f;


    spread = 0.5f * sin(iTime) + 0.5f;



    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //Correct uv to always be a circle
    vec2 circle_uv = uv - center;
    float resY = iResolution.y;
        circle_uv.y *= resY / iResolution.x;//If you wat an oval, remove this line


    //Calculate distortion in the ring

    //First, we get some noise to make the distortion a little more interesting
    //I think .025 is a nice strength
    float noise = noise_str * distance(texture(iChannel1, uv).xy, vec2(0.0, 0.0));

    float outer = smoothstep(spread - width + noise, spread + noise, length(circle_uv));

    float inner = smoothstep(spread - width * inner_dist + noise, spread - width + noise, length(circle_uv));

    //Multiply to get just the area between
    float ring = inner * outer;



    //Sample texture w/ distortion
    vec2 displacement = normalize(uv - center) * strength * ring;

    vec4 col = texture(iChannel0, uv - displacement);


    //Sample inner texture
    //Note: If you want no inner texture, and just the shockwave
    //Either use iChannel0 again, or just cut all code from here down to output
    vec4 color_to_mix = texture(iChannel2, uv - displacement);


    //Mix textures together

    //How strong the color should be on the inside
    col = mix(col, color_to_mix, 1.0 * inner);
    //How strong the color should be in the ring
    col = mix(col, color_to_mix, clamp(2.0 * ring, 0.0, 1.0));




    // Output to screen
    fragColor = col;
}

