#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)

#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)

#define vel(v) (v)*inversesqrt(1.+dot(v,v))

#define inside(v) ((v).x>=0.&&(v).y>=0.&&(v).x<R.x&&(v).y<R.y)

float ln (vec3 p, vec3 a, vec3 b) { 
    return length(p-a-(b-a)*(dot(p-a,b-a)/dot(b-a,b-a)));
}

Main {

    Q = A(U);
    vec4 o = B(U);
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    if (abs(x)!=abs(y)) {
        vec2 v = vec2(x,y);
        vec2 W = U+v;
        vec4 q = A(W);
        vec4 b = B(W);
        Q.xy -= .125*pow(q.w,10.)*(q.w*mat2(b)-mat2(1))*v/dot(v,v);
    }
    //Q.y -= .01/R.y;
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.xy *= 0.;
    if (iFrame < 1) {
        Q.w = .8*exp(-.5*length(U-.5*R)/R.y);

    }
    if (iMouse.z>0.) Q.xy += .01*(U-iMouse.xy)/(1.+length(U-iMouse.xy))*exp(-.1*length(U-iMouse.xy));

    if (U.x<3.||R.x-U.x<3.||R.y-U.y<3.||U.y<3.)Q.xy *= 0.;
}

Main {

    Q = A(U);
    vec4 o = B(U);
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    if (abs(x)!=abs(y)) {
        vec2 v = vec2(x,y);
        vec4 q = A(U+v);
        vec4 b = B(U+v);
        Q -= .25*b.w*vec4(b.xy*v.x,b.xy*v.y)/dot(v,v);
    }

    if (iFrame < 1) Q = vec4(1,0,0,1);
}

Main {

Q = A(U);
vec4 dQ = vec4(0);
#define K (1./8.)
for (int x = -1; x <= 1; x++)
for (int y = -1; y <= 1; y++)
if (x!=0||y!=0){///abs(x)!=abs(y)) {
    vec2 v = vec2(x,y);
    vec2 W = U+v;
    vec4 q = A(W);

    vec2 a = vel(Q.xy),
         b = vel(q.xy)+v;
    float ab = dot(v,b-a);
    if (true) {
        float i = dot(v,(0.5*v-a))/ab;
        float j = .5;
        float k = .5;
        float wa = K*Q.w*min(i,j)/j;
        float wb = K*q.w*max(k+i-1.,0.)/k;
        dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
    } else {
        dQ += K*Q.w*vec4(Q.xyz,1);
    }

}
if (dQ.w>0.) dQ.xyz /= dQ.w;
Q = dQ;
}

Main {

Q = A(U);
vec4 O = B(U);
vec4 dQ = vec4(0);
float w = 0.;
#define K (1./8.)
for (int x = -1; x <= 1; x++)
for (int y = -1; y <= 1; y++)
if (x!=0||y!=0){///abs(x)!=abs(y)) {
    vec2 v = vec2(x,y);
    vec2 W = U+v;
    vec4 q = A(W);
    vec4 o = B(W);

    vec2 a = vel(Q.xy),
         b = vel(q.xy)+v;
    float ab = dot(v,b-a);
    if (true) {
        float i = dot(v,(0.5*v-a))/ab;
        float j = .5;
        float k = .5;
        float wa = K*Q.w*min(i,j)/j;
        float wb = K*q.w*max(k+i-1.,0.)/k;
        dQ += O*wa+o*wb;
        w += wa+wb;
    } else {
        dQ += K*Q.w*O;
        w += K*Q.w;
    }

}
if (w>0.) dQ /= w;
Q = dQ;
}
