void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Values from -1 to 1
    float t = uv.x * 2. - 1.;

    // Cluster colors
    float m = round(0.5 + uv.y * 8.);
    if (m < 8.0) {
        t = round(t*(m*m+0.5)) / (m*m);
    }

    // Apply colormap
    vec3 col = max(vec3(0.), vec3(
        1. + 3.*t,
        (1. + 3. * t * t) ,
        1. - 3.*t)) / 4.;

    // Output to screen
    fragColor = vec4(col, 1.);
}
