// Complex Log By Rik Riesmeijer, 2024 - No rights reserved.
void mainImage(out vec4 c, vec2 z) {
    z.yx = z / iResolution.xy * 2.0 - 1.0;
    z = vec2(log(length(z)), 
         z.x > 0.? atan(z.y / z.x)
                 : atan(z.y / z.x) + 3.1415) / 2.0 * 3.1415;
    c = 0.5 + 0.5 * cos(vec4(z.xyx + vec3(1, 2, 4) - 2.0 * iTime, 1));
}
