float circlesdf(vec2 uv, float radius) {
    return length(uv) - radius;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mp = iMouse.xy / iResolution.xy * 2.0 - 1.0;
    mp.x *= iResolution.x / iResolution.y;

    float mouse_dist = 1.0 - abs(distance(uv, mp) * 4.);

    int depth = 90;
    vec3 col = vec3(0.0);

    if (circlesdf(uv, 0.6) < 0.01) {
        vec2 dir = normalize(mp - uv);
        float delta = 1.0 / float(5000);

        for (int i = 0; i < 10000; i++) {
            uv += delta * dir;

            if (circlesdf(uv, 0.6) < 0.01) {
                if (depth <= 0) {
                    break;
                }
                depth -= 1;
            }

            if (distance(uv, mp) < 0.001) {
                col = vec3(1.0, 0.0, 0.0);
                break;
            }
        }
    }
    mouse_dist = min(circlesdf(uv,0.6),mouse_dist);
    vec3 mouseLight = vec3(1.0, 0.0, 0.0);
    col = max(mouse_dist * mouseLight, col);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
