float Hash21(vec2 p) {
    p = fract(p*vec2(424.34,342.21));
    p += dot(p, p+34.32);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 3.;
    vec3 col = vec3(0);

    float pulse = sin(abs(uv.x))*.5+.8; 
    uv.x = abs(uv.x);
    for(float i = 1.;i <= 10.; i++){
        float b = 1.1-sin(uv.y+iTime)*.1+.1;
        vec2 coord = uv-vec2(uv.x*b, -1./i);
        //coord.x *= sin(uv.x+iTime)/i*2.+sin(uv.x);
        col += .01/(i*length(coord))*pulse*1.5;
    }
    col += smoothstep(0., -.2, uv.y)*.1*vec3(.1, .3, .8);
    col += .15/length(uv-vec2(0, .8));
    col -= smoothstep(.4, 0., length(uv-vec2(0., .8)))*.4;

    fragColor = vec4(col,1.0);
}
