vec2 f_o_rotd(vec2 o_trn, float n_ang_nor){
    float n_tau = radians(360.);
    float n_sin = sin(n_ang_nor*n_tau);
    float n_cos = cos(n_ang_nor*n_tau);
    mat2 o_matr = mat2(
        n_cos, -n_sin, 
        n_sin, n_cos
    );
    return o_trn*o_matr;
}
float fns(float n, float b){
    return sin(n+b)*.5+.5;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_scl_min = min(iResolution.x, iResolution.y);
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/n_scl_min;
    //o_trn*=fns(iTime,0.)+4.;
    float n_dcntr = length(o_trn);
    o_trn*=3.;
    float n_tau = radians(360.);
    float n_circles = 1.;
    float n_ang_nor = fract(atan(o_trn.x, o_trn.y)/n_tau+(1./n_circles/2./2.));
    float n = length(o_trn);
    float norg = n;
    n_ang_nor = n_ang_nor+sin(n*n_tau+iTime)*.009+iTime*.01;
    float n1=pow(n, 1./2.);
    float n2 = pow(n, 2.);
    float t = fns((1./(n+.1)*3.),iTime);
    float n3 = t*n1+((1.-t)*n2);
    n = n3;
    float ntmp = 1.-pow(fns(norg*3.-iTime*2.,0.),1./3.);

    float s = n_tau*n*1.*sin(n_ang_nor*n_tau*5.)*sin(1./n);
    vec3 of = vec3(1.,2.,3.)*ntmp*2.;
    vec3 on = vec3(
        fns(n*s,of[0]),
        fns(n*s,of[1]),
        fns(n*s,of[2])
    );
    vec3 odc2 = vec3(
        on
    );

    //odc2 = vec3(sin(n_dcntr1*n_tau*2.+iTime));
    fragColor = vec4(odc2,1.);
}
