Main {

    Q = A(U*.1);

    Q.x = exp(-abs(U.y-R.y*Q.x)/(1.+R.x*abs(dFdx(Q.x))));
    Q.y = exp(-abs(U.y-R.y*Q.y)/(1.+R.x*abs(dFdx(Q.y))));

    //Q += Q.w;

}

Main {

    Q = A(U);
    vec4 a = A(U+vec2(1,0));
    vec4 b = A(U-vec2(1,0));

    vec4 _a = a.w*(a-Q);
    vec4 _b = b.w*(Q-b);


    Q.x += .5*(_a-_b).x;
    Q.y = (a.w*a.y+b.w*b.y)/(a.w+b.w);

    if (U.x < 1.) Q = vec4(1,1,0,0);
    if (U.x > .1*R.x) Q = vec4(0,0,0,0);


    if (iFrame < 1) Q = vec4(0);

    float k = 1.;
    if (U.x < .0333*R.x) k = .5;
    else if (U.x < .066*R.x) k = .25;
    else                   k = 1.;
    Q.w = k;


}

