void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

//const bool play = false;
const int scale = 8;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 newFragCoord = ivec2(fragCoord/float(scale));
    ivec2 newMouseCoord = ivec2(iMouse/float(scale));

    int center = int(texelFetch(iChannel0, ivec2(fragCoord), 0).r);

    int neighbor =  int(texelFetch(iChannel0, (ivec2(newFragCoord) + ivec2(-1, 1))*scale, 0).r) +
                    int(texelFetch(iChannel0, (ivec2(newFragCoord) + ivec2(0, 1))*scale, 0).r) +
                    int(texelFetch(iChannel0, (ivec2(newFragCoord) + ivec2(1, 1))*scale, 0).r) +
                    int(texelFetch(iChannel0, (ivec2(newFragCoord) + ivec2(-1, 0))*scale, 0).r) +
                    int(texelFetch(iChannel0, (ivec2(newFragCoord) + ivec2(1, 0))*scale, 0).r) +
                    int(texelFetch(iChannel0, (ivec2(newFragCoord) + ivec2(-1, -1))*scale, 0).r) +
                    int(texelFetch(iChannel0, (ivec2(newFragCoord) + ivec2(0, -1))*scale, 0).r) +
                    int(texelFetch(iChannel0, (ivec2(newFragCoord) + ivec2(1, -1))*scale, 0).r);

    float col = float(center);

    bool play = bool(texelFetch(iChannel1, ivec2(0, 0), 0).r);

    if(play == true)
    {
        if(center == 1)
        {
            if(neighbor < 2 || neighbor > 3)
            {
                col = 0.0;
            }
        }
        else
        {
            if(neighbor == 3)
            {
                col = 1.0;
            }
        }
    }


    bool mask = newFragCoord.x == newMouseCoord.x && newFragCoord.y == newMouseCoord.y;

    if(iMouse.z >= 0.5)
    {
        col = mask ? 1.0-col : col;
    }


    fragColor = vec4(vec3(col), 1.0);
}

const int KEY_LEFT  = 37;
const int KEY_RIGHT = 39;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float val = texelFetch(iChannel1, ivec2(0,0), 0).r;
    if(texelFetch(iChannel0, ivec2(KEY_LEFT, 0), 0).r > 0.5)
    {
        val = 0.0;
    }
    else if(texelFetch(iChannel0, ivec2(KEY_RIGHT, 0), 0).r > 0.5)
    {
        val = 1.0;
    }


    fragColor = vec4(val,0.0,0.0,1.0);
}
