#define PI2 6.28
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy/2.0)/iResolution.y;

    float r = .35;
    float rs = .1;
    float speed = .1;
    float numBalls = 40.;
    vec3 col = vec3(0);
    for (float f = 0.; f < PI2; f += PI2/numBalls)
    {
        vec2 pos = vec2(sin(iTime*speed*.57+f)*.3, cos(iTime*speed*1.6+f)*.3);
        float d = distance(uv, pos);
        float c = smoothstep(r, r-rs, d);
        float fade = 1.0-(PI2-f*.2)/PI2;
        float rod = c-.1;
        float gul = c-.2;
        float bla = c-.4;
        col += vec3(rod*fade, gul*fade, bla*fade);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
