    vec2 coord = fragCoord / iResolution.xy;
    vec3 col = texture(iChannel0, coord).xyz;
    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (2.0*fragCoord - iResolution.xy)/min(iResolution.x, iResolution.y);

    float t = iTime * 0.6;
    float c = cos(t);// - 0.125 * sin(4. * t));
    float s = sin(t);// - 0.125 * sin(4. * t));

    vec2 d1 = vec2(c, s);
    vec2 d2 = vec2(-s, c);

    mat2x2 m = mat2x2(c, s, -s, c) * max(abs(c), abs(s));

    vec2 center = vec2(0.0);
    vec2 p2 = 10.0 * (p - center);

    // rotated+stretched position
    vec2 tp = m * p2;
    bool b = false;
    vec2 mod_tp = vec2(mod(tp.x + 0.5, 1.0) - 0.5, mod(tp.y + 0.5, 1.0) - 0.5);
    vec2 trunc_tp = tp - mod_tp;
    mat2x2 inv_m = inverse(m);
    vec2 div_tp = inv_m * trunc_tp;
    //if (abs(tp.x - mod_tp.x) + abs(tp.y - mod_tp.y) < 8.0 + 0.5) {
        float k = 0.4;
        vec2 diff1 = p2 - div_tp;
        if (max(abs(diff1.x), abs(diff1.y)) < k) {
            b = true;
        }
    //}
    float taxicab = abs(trunc_tp.x) + abs(trunc_tp.y);
    float brightness = mod(round(taxicab), 2.0) == 0.0 ? 1.0 : 0.5;
    vec3 col = b ? vec3(brightness, brightness * 0.5, 0.0) : vec3(0.0);

    vec2 coord = fragCoord / iResolution.xy;
    vec3 oldCol = texture(iChannel0, coord).xyz;
    float r = 0.2;
    fragColor = vec4(r * col + (1. - r) * oldCol, 1.0);
}
