void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xx * 3.5;

    vec3 rects[20];
    rects[0] = vec3(0.81, 0.0, 0.0);
    rects[1] = vec3(0.62, 0.0, 0.81);
    rects[2] = vec3(0.52, 0.0, 1.43);
    rects[3] = vec3(0.47, 0.0, 1.95);
    rects[4] = vec3(0.43, 0.0, 2.42);
    rects[5] = vec3(0.44, 0.81, 0.0);
    rects[6] = vec3(0.19, 0.62, 0.81);
    rects[7] = vec3(0.1, 0.52, 1.43);
    rects[8] = vec3(0.05, 0.47, 1.95);
    rects[9] = vec3(0.04, 0.43, 2.42);
    rects[10] = vec3(0.44, 1.25, 0.0);
    rects[11] = vec3(0.56, 0.81, 0.44);
    rects[12] = vec3(0.53, 0.62, 1.0);
    rects[13] = vec3(0.47, 0.52, 1.53);
    rects[14] = vec3(0.46, 0.47, 2.0);
    rects[15] = vec3(0.35, 1.69, 0.0);
    rects[16] = vec3(0.32, 1.37, 0.44);
    rects[17] = vec3(0.22, 1.15, 1.0);
    rects[18] = vec3(0.16, 0.99, 1.53);
    rects[19] = vec3(0.06, 0.93, 2.0);


    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    for(int i = 0; i < 20; i++) {
        float x = rects[i][2];
        float y = rects[i][1];
        float s = rects[i][0];
        if (x <= uv.x && 
                uv.x < x + s &&
            y <= uv.y &&
                uv.y < y + s) {
            col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0+i,2+i,4+i));
        }
    }


    // Time varying pixel color



    // Output to screen
    fragColor = vec4(col,1.0);
}
