// Shader Advent Calendar 2024, #22
// Read article here:
// https://github.com/mrange/shader-advent-2024/blob/main/day-22/README.md
//
// Basic halftone example (applied to video) - (this one!)
// Halftone dot shapes illustration          - https://www.shadertoy.com/view/lfyfRc
// Halftone - 3 kinds                        - https://www.shadertoy.com/view/XcKfWc


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float lum = texture(iChannel0, fragCoord/iResolution.xy).r - 0.1;

    vec2 gridUv = fragCoord/iResolution.yy;
    float gridSize = 60.;

    // create grid
    gridUv = mod(gridUv * gridSize, 1.0);

    // for each grid-cell, just take the distance to center
    // and divide by the length of half the diagonal 
    float halftone = length(gridUv - 0.5) / length(vec2(0.5, 0.5));

    // threshold
    float c = halftone < lum ? 1.0 : 0.0;

    fragColor = vec4(c,c,c,1.0);
}
