float layer[100];
int maxLayer = 0;

float drawLayer(vec2 uv, float vertices, float t, float amplitude, float gradient, float smallest,float tOffset, float sOffset){
    float r = 0.2;
    float d = length(uv);
    float s = abs(sin(iTime*2.+tOffset)/sOffset);
    float wobble = r*(s+smallest) + amplitude * cos((atan(uv.y,uv.x)*vertices)+iTime*t);
    if(maxLayer > 0){
        float m = smoothstep(wobble+gradient,wobble,d);
        layer[maxLayer] = smoothstep(wobble,wobble+0.05,d);
        for(int i=0;i<maxLayer;i++){
            m *= layer[i];
        }
        maxLayer++;
        return m;
    }
    else{
        layer[0] = smoothstep(wobble,wobble+0.05,d);
        maxLayer++;
        return smoothstep(wobble+gradient,wobble,d);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy / iResolution.xy; //This normalizes the canvas so that it ranges from 0-1 not from 0 to sizeX

    uv = (uv - 0.5)*2.0;//Makes (0,0) the center of the screen
    uv.x *= iResolution.x / iResolution.y;//Makes the image be the same no matter the aspect ratio

    float sharpness = 0.003;

    vec3 totalCol = vec3(0,0,0);

    vec3 col0 = vec3(0.4,0.0,1);

    vec3 col1 = vec3(1.,0.,0.);

    vec3 col2 = vec3(0.,1.,0.);

    vec3 col3 = vec3(0.,0.,1.);

    vec3 col4 = vec3(1.,0.,1.);

    vec3 col5 = vec3(0.,1.,1.);

    vec3 col6 = vec3(1.,1.,0.);

    vec3 col7 = vec3(0.,0.,1.);

    vec3 col8 = vec3(1.,0.,1.);

    vec3 col9 = vec3(0.,1.,1.);

    vec3 col10 = vec3(1.,1.,0.);


    // drawLayer(vec2 uv, float vertices, float t, float amplitude, float gradient, float smallest,float tOffset, float sOffset)

    col0 *= drawLayer(uv,4.,-8.,0.02,sharpness,0.5,-2.,4.);

    col1 *= drawLayer(uv,5.,6.,0.02,sharpness,1.,0.,3.);

    col2 *= drawLayer(uv,7.,-4.,0.02,sharpness,2.,5.,3.);

    col3 *= drawLayer(uv,10.,8.,0.02,sharpness,3.,-12.,3.);

    col4 *= drawLayer(uv,12.,-11.,0.02,sharpness,4.,9.,3.);

    col5 *= drawLayer(uv,15.,21.,0.02,sharpness,5.,18.,3.);

    col6 *= drawLayer(uv,17.,-6.,0.02,sharpness,6.,12.,3.);

    col7 *= drawLayer(uv,20.,8.,0.02,sharpness,7.,-12.,3.);

    col8 *= drawLayer(uv,21.,-22.,0.02,sharpness,8.,9.,3.);

    col9 *= drawLayer(uv,24.,21.,0.02,sharpness,9.,18.,3.);

    col10 *= drawLayer(uv,6.,-6.,0.02,sharpness,11.,12.,3.);

    totalCol += col0;
    totalCol += col1;
    totalCol += col2;
    totalCol += col3;
    totalCol += col4;
    totalCol += col5;
    totalCol += col6;
    totalCol += col7;
    totalCol += col8;
    totalCol += col9;
    totalCol += col10;


    fragColor = vec4(totalCol,1.0);
}
