#define grid 8.
#define speed 4.

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float margin = pow(fract(iTime/speed),2.)/2.;
    float invert = floor(iTime/speed);

    vec2 uv = fragCoord/iResolution.y;
    float aspect = iResolution.x/iResolution.y;

    vec2 timeuv=uv-iTime*.1;
    uv.x+=(1.-aspect)/2.;

    vec2 id = floor(timeuv*grid);

    vec2 griduv=fract(timeuv*grid);

    float rng = hash12(id);


    if(rng>.5){griduv.x=1.-griduv.x;}

    bool type=false;

    float dist = distance(griduv.xy,vec2(0.,0.));
    type=dist<.5+margin && dist>.5-margin;
    dist = distance(griduv.xy,vec2(1.,1.));
    type=type||(dist<.5+margin && dist>.5-margin);

    bool rings = mod(floor(distance(vec2(.5,.5),uv.xy)/.2-iTime),2.)==0.;
    vec3 bg = rings?vec3(.1608,.6784,1.):vec3(1.,.4667,.6588);
    if(mod(invert,2.)==0.){type=!type;}
    vec3 col=type?bg:vec3(1.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
