void mainImage( out vec4 fragColor, in vec2 uv){

    uv.x = 10.*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 10.*(uv.y-0.5*iResolution.y)/iResolution.y;

    vec3 col = vec3(0.);
    float pi = 3.141592;
    float c=float(iFrame)/100.;
    float a=1.+mod(floor(c/(2.*pi)),3.);

    for(float s=0.; s<=3.75; s+=0.25){

    float val = pow(uv.x-2.*cos(c+s*pi/2.),2.)+pow(uv.y+2.*sin(c+s*pi/2.),2.);
    val=(0.1+(1.+0.2*(1.+sin(c+3.5*pi))*cos(uv.x*uv.y))*val);

    float v1=0.;
    float v2=0.;
    float v3=0.;

    for(float n=0.; n<=10.; n+=0.25*pow(2.,a)){
    v1+=pow(2.,-5.*abs(val-0.2*n));
    v2+=pow(2.,-5.*abs(val-0.4*n));
    v3+=pow(2.,-5.*abs(val-0.6*n));
    }

    col+=0.025*pow(2.,a)*vec3(v1,v2,v3);   
    }

    fragColor = vec4(col,1.0);
}
