#define S(a,b) mix(1., a, sin(b)*.15+.5)
#define s (iTime*.1-1.)
#define n iResolution

void mainImage(out vec4 O, in vec2 F) {
    vec3 a,r,t = normalize(vec3((10.*F.xy-n.xy)/n.y*S(8.,s+2.100), 1.)); 
    float i, d, l=1.;
    while (i++ < 100. && d<100. && l>0.001) {
        a = r+d*t; a.z+=s;
        d += l = length(mod(a+2.,7.)-2.) - step(1.,abs(a.y))+.001;
    }
    O.rgb = mix(cos(S(2.3,s)*vec3(1.02,7.04,3.06)*d+s)*.3+.7, vec3(1.), d*.009);
}
