const int NUM_TAPS = 9;

float distortionAmount = 0.01;
float speed = 15.85;
float blurCoef = 5.0;

const vec2 PoissonDisc[NUM_TAPS]
        = vec2[NUM_TAPS]( vec2(-0.220147, 0.976896),
                          vec2(-0.735514, 0.693436),
                          vec2(-0.200476, 0.310353),
                          vec2( 0.180822, 0.454146),
                          vec2( 0.292754, 0.937414),
                          vec2( 0.564255, 0.207879),
                          vec2( 0.178031, 0.024583),
                          vec2( 0.613912,-0.205936),
                          vec2(-0.385540,-0.070092) );

vec4 blur(float disc_radius, vec2 uv)
{
    vec4 cOut;
    for (int step = 0; step < 9; step++)
    {
        vec2 offset = PoissonDisc[step] * disc_radius / iResolution.xy;
        vec2 coord = uv + offset;

        cOut += texture(iChannel0, coord);

    }
    return cOut / 9.f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    //// Haze uv distortion
    vec2 distUV = uv + vec2(0, sin(uv.x * iResolution.x * 0.3 + iTime * speed) * distortionAmount);

    fragColor = blur(blurCoef, distUV);
}
