void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;

    vec2 cir1 = vec2(0.7 + 0.2 * sin(1.5*iTime), 0.5 + 0.3 * cos(iTime)); 
    vec2 cir2 = vec2(0.7 + 0.2 * cos(1.5*iTime), 0.5 + 0.3 * sin(1.5*iTime)); 
    vec2 cir3 = vec2(0.3, 0.5);

    vec3 col1 = vec3(cir1.x, cir1.y, 0.5);
    vec3 col2 = vec3(cir2.x, cir2.y, 0.1);
    vec3 col3 = vec3(cir2.y, cir2.x, 1.5);

    float dist1 = length(fragCoord - res*cir1);
    float dist2 = length(fragCoord - res*cir2);
    float dist3 = length(fragCoord - res*cir3);

    vec3 bg = vec3(0.1*tan(iTime+ 0.5 * fragCoord.xyy*0.2)) / 0.4;

    float cirR = 0.1*res.x;
    float cirR2 = 0.1*res.y;
    float cirR3 = 0.15*res.x;

    if(dist1 < cirR) bg = col1;
    if(dist2 < cirR2) bg = col2;
    if(dist3 < cirR3) bg = col3;

    fragColor = vec4(bg, 2.0);
}
