#define S(a,b) mix(1., a, sin(b)*.15+.5)
#define n iResolution

void mainImage(out vec4 O, in vec2 F) {
    float s = (1.0-abs(tanh(iTime)))*iTime*iTime*0.002 + iTime*.1-1.0;
    vec3 a,r,t = normalize(vec3((10.*F.xy-n.xy*5.0)/n.y*S(8.,s+2.100), 1.)); 
    float i, d, l=1.;
    while (i++ < 100. && d<100. && l>0.001) {
        a = r+d*1.0*t; a.z+=s;
        d += l = length(mod(a+2.,7.)-2.) - step(1.,abs(a.y))+.001;
    }
    O.rgb = mix(-cos(S(2.3,s)*vec3(2.04,7.04,7.04)*d+s)*.3+.3, vec3(0.01), d*.009);
}
