void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = abs( u+u - R ) / R.y,
         I = floor( 2.93* U* mat2(1,1,-1,1) ),   // tiles
         S = cos(92.*U); S /= fwidth(S);        // strips
             // 2pi*14.625* U
    float s = sign( min(I.x,I.y) < 1. && max(I.x,I.y) < 3.
                      ? S = -S, .5 - mod(I.x+I.y,2.)
                      :            U.y - U.x+.342
                  );
    O = vec4(.5 + s* S[ s > 0. ? 0:1 ] );
}
