#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}


float GetDist(vec3 p) {

    p.yz *= Rot(sin(iTime+p.x)/2.-1.);

    float d = sdBox(p, vec3(1));

    float c = cos(3.1415/5.), s = sqrt(0.75-c*c);
    vec3 n = vec3(-0.5, -c, s);

    p = abs(p);
    p -= 2.*min(0., dot(p, n))*n;

    p.xy = abs(p.xy);
    p -= 2.*min(0., dot(p, n))*n;

    p.xy = abs(p.xy);
    p -= 2.*min(0., dot(p, n))*n;  // rhombic trichondahedron


    d = p.z-1.;

    return d;
}

float RayMarch(vec3 ro, vec3 rd, float side) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p)*side;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.01, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;
    uv*=3.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
    vec3 ro = vec3(0, 3, -3)*.8;
    ro.yz *= Rot(-m.y*PI+1.+gv.x);
    ro.xz *= Rot(-m.x*TAU+gv.y);

    vec3 rd = GetRayDir(gv, ro, vec3(0,0.,0), 2.);
    vec3 col = texture(iChannel0, rd).rgb;

    float d = RayMarch(ro, rd, 1.); // raymarch the outside.

    float IOR = 2.45; //index of refraction of glass.
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);
        vec3 refOutside = texture(iChannel0, r).rgb;

        vec3 rdIn = refract(rd, n, 1./IOR);

        vec3 pEnter = p - n*SURF_DIST*3.;
        float dIn = RayMarch(pEnter, rdIn, -1.);

        vec3 pExit = pEnter + rdIn * dIn;
        vec3 nExit = -GetNormal(pExit);

        vec3 reflText = vec3(0);

        vec3 rdOut = vec3(0);

        float abb = .05;

        //red
        rdOut = refract(rdIn, nExit, IOR-abb);
        if(dot(rdOut, rdOut)==0.) rdOut = reflect(rdIn, nExit);
        reflText.r = texture(iChannel0, rdOut).r; 

        //green
        rdOut = refract(rdIn, nExit, IOR);
        if(dot(rdOut, rdOut)==0.) rdOut = reflect(rdIn, nExit);
        reflText.g = texture(iChannel0, rdOut).g; 

        //blue
        rdOut = refract(rdIn, nExit, IOR+abb);
        if(dot(rdOut, rdOut)==0.) rdOut = reflect(rdIn, nExit);
        reflText.b = texture(iChannel0, rdOut).b; 

        float dens = .2;
        float optDist = exp(-d*dens);

        reflText *= optDist;//+vec3(1.,0.5, .5) ;

        float fresnel = pow(1.+dot(rd, n), 5.);
        float cc = fract(sin(-length(uv)+iTime)/.5);

        col += vec3(3.*cc, .2, 6.*cc);
        col.rg -= sin(abs(id.x+.5)+abs(id.y+.5))*2.;

        col *= mix(reflText, refOutside, fresnel);

        //col = n*.5+.5;
    }
    //col += .3/length(uv-1.);
    //col *= vec3(.3, .05, .5);
    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
