#define S(a,b,t) smoothstep(a,b,t)
#define NUM_LAYERS 8.0
#define MOD3 vec3(.1031,.11369,.13787)

// 随机函数
float hash11(float p) {
    vec3 p3 = fract(vec3(p)*MOD3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.x+p3.y)*p3.z);
}

// 在半径1的圆内均匀随机分布点
vec2 randomInCircle(float seed) {
    float r = sqrt(hash11(seed));
    float theta = hash11(seed+23.45)*6.28318;
    return vec2(cos(theta), sin(theta))*r;
}

// 线段距离函数
float df_line(vec2 a, vec2 b, vec2 p) {
    vec2 pa = p - a, ba = b - a;
    float h=clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
    return length(pa - ba*h);
}

float lineDist(vec2 a, vec2 b, vec2 p) {
    float d = df_line(a,b,p);
    float r1=0.03, r2=0.005;
    return S(r1,r2,d);
}

// 单层模式生成函数
float LayerPattern(vec2 uv, float layerIndex, float t) {
    // 固定scale范围，让图案更集中在中央
    float scale = mix(1.5, 6.0, fract(layerIndex*0.31+sin(t*0.07)));
    uv *= scale;

    float seed = layerIndex*37.23 + floor(t)*1.7;

    // 增加点的数量为25个，使图案更密集
    int N=25; 
    vec2 points[25];
    for(int i=0;i<N;i++){
        float sid = float(i)+seed;
        vec2 p = randomInCircle(sid);
        // 时间扰动使点随时间略有微动
        p += vec2(sin(t+sid), cos(t*1.1+sid*2.0))*0.1;
        points[i]=p;
    }

    float m=0.0;
    // 点的发光
    for(int i=0;i<N;i++){
        float d=length(uv - points[i]);
        float val=S(0.04,0.01,d);
        m+=val*0.7; 
    }

    // 点之间的连线
    for(int i=0;i<N;i++){
        for(int j=i+1;j<N;j++){
            float d=lineDist(points[i],points[j],uv);
            float dist=length(points[i]-points[j]);
            float fade=S(1.0,0.2,dist);
            m+=d*fade*0.4;
        }
    }

    // 根据层index和时间进行层的淡入淡出
    float fade = S(0.0,0.8,fract(t+layerIndex))*S(1.0,0.7,fract(t+layerIndex));
    return m*fade;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
    // 标准化uv，以屏幕中心为原点
    vec2 uv=(fragCoord - iResolution.xy*0.5)/iResolution.y;
    float t = iTime*0.3;

    // 不再旋转和鼠标偏移，始终居中
    // uv不做任何额外变换

    float m=0.0;
    // 增加层数至8层，以获得更丰富的结构
    for(float i=0.0;i<NUM_LAYERS;i++){
        float layerVal=LayerPattern(uv,i,t);
        float depthFade=S(0.0,1.0,1.0 - i/NUM_LAYERS);
        m+=layerVal*depthFade;
    }

    // 色彩映射：使用随时间变化的柔和渐变
    vec3 baseCol=vec3(sin(t*0.9)*0.5+0.5, cos(t*1.1)*0.5+0.5, sin(t*1.3+0.5)*0.5+0.5);
    baseCol=baseCol*0.6+0.4;
    vec3 col=baseCol*m;

    // 中心晕影
    float rad=dot(uv,uv);
    col*=1.0-rad*0.3;

    // 无闪烁，全局亮度不再变化
    // 保持持续明亮

    fragColor=vec4(col,1.0);
}
