void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 fractUv = vec2(fract(uv.x * 8.), fract(uv.y * 2.));

    vec3 magic = vec3(fractUv.x, fractUv.y, fractUv.x);

    float t = iTime + uv.x * 4.;

    vec3 col = 0.5 + 0.5*cos(t+magic+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
