vec3 palette( float t ){
     vec3 a = vec3(sin(iTime)*0.5 + 0.5,sin(iTime*1.25+1.)*0.5 + 0.5,sin(iTime*0.6+2.4)*0.5 + 0.5);
     vec3 b = vec3(.2,.7,.6);
     vec3 c = vec3(.5,1.,1.);
     vec3 d = vec3(0.716,0.91,0.112);
     return a+b*-sin( acos(-1.)*2.*(c+t*d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;

    uv.x*=iResolution.x/iResolution.y;

    vec2 uv0=uv;

    vec3 fcol =vec3(0.);

    for (float i =0.0; i<4.; i++){
        uv.x-=sin(uv.y*4.+iTime)/4.;

        uv.y+=cos(uv.x*4.+iTime)/4.;

        float d=(sin(uv.x*8.+iTime)/sin(uv0.y+sin(iTime+uv0.x*2.)))*exp(-length(uv0));

        float x=length(uv)*exp(-length(uv0));

        d=sin(d*8.+iTime)/8.;

        x=sin(x*4.+iTime)/4.;

        d=abs(d);

        d=pow(0.01/d,1.2);

        vec3 col=sin(palette(length(uv0) +i*i*0.4+ iTime*0.4));

        fcol+=col*d+x*col;

    }

    fragColor = vec4(fcol,1.);
}
