float tri_sin( in float x ) {
    float m = mod(2.0*(x*3.141593)-1.0,4.0)-2.0;
    return m*sign(m) - 1.0;
}

float exp_mix0( in float a, in float b, in float c, in float s ) {
    return a+(b-a)*( c<0.5 ? (1.0-pow(1.0-2.0*c,s))*0.5 : pow(2.0*c-1.0,s)*0.5+0.5 );
}
float exp_mix1( in float a, in float b, in float c, in float s ) {
    return a+(b-a)*( c<0.5 ? pow(2.0*c,s)*0.5 : (1.0-pow(2.0-2.0*c,s))*0.5+0.5 );
}
float exp_mix_div( in float a, in float b, in float c, in float s ) {
    return 
    s>1.0?
    exp_mix1(a,b,c,s)
    :
    exp_mix0(a,b,c,2.0-s);
}

vec3 h2rgb( in float h, in float s ) {
    h *= 360.0;
    float R = max(0.0,2.0*h-480.0)+max(0.0,-2.0*h+240.0);
    R = exp_mix_div( 0.0, 1.0, min(120.0,R)/120.0 - 0.000001, s );
    float G = min(120.0,2.0*h)+min(120.0,-2.0*h+480.0)-120.0;
    G = exp_mix_div( 0.0, 1.0, max(0.0,G)/120.0 - 0.000001, s );
    float B = min(120.0,2.0*h-240.0)+min(120.0,-2.0*h+720.0)-120.0;
    B = exp_mix_div( 0.0, 1.0, max(0.0,B)/120.0 - 0.000001, s );
    return vec3( R, G, B );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = h2rgb(fragCoord.x/iResolution.x, tri_sin(iTime/20.0)*5.0);

    fragColor = vec4(col,1.0);
}
