void mainImage( out vec4 O, in vec2 U )
{
    vec2 R = iResolution.xy,
         u = (U+U - R) / R.y;

    float y = floor(u.y*50.);
    int iy = int(y);

    O = vec4(mod(y,2.));

    float d = length(max(abs(u)-vec2(.4,.5),0.));

    // 注释下面这一行，看效果（这行是用来在方块四周简单添加蓝色和绿色的）
    if(d>0.)O*=.6,O.yz += .6;    

    O -= .2;
}
