// A simple way to create color variation in a cheap way (yes, trigonometrics ARE cheap
// in the GPU, don't try to be smart and use a triangle wave instead).

// See https://iquilezles.org/articles/palettes for more information



//modifications to allow control over the variables separately, and having RGB channels below the main palette


vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    // animate
    p.x += 0.03*iTime; //how fast it slides over. do 0*iTime to make it not move

    //values
float aRed=.5;    float bRed=.5;  float cRed=.5;  float dRed=0.;  //must give values with a decimal. e.g. 0.0, not 0

float aBlue=.5;  float bBlue=.5;  float cBlue=.65;  float dBlue=0.3;

float aGreen=.5;  float bGreen=.5;  float cGreen=1.2;  float dGreen=.54;

    // compute colors
    vec3                col = pal( p.x, vec3(aRed,0,0),vec3(bRed,0,0),vec3(cRed,0,0),vec3(dRed,0,0) );
    if( p.y>(1.0/7.0) ) col = pal( p.x, vec3(0,aGreen,0),vec3(0,bGreen,0),vec3(0,cGreen,0),vec3(0,dGreen,0) );
    if( p.y>(2.0/7.0) ) col = pal( p.x, vec3(0,0,aBlue),vec3(0,0,bBlue),vec3(0,0,cBlue),vec3(0,0,dBlue) );
    if( p.y>(3.0/7.0) ) col = pal( p.x, vec3(aRed,aGreen,aBlue),vec3(bRed,bGreen,bBlue),vec3(cRed,cGreen,cBlue),vec3(dRed,dGreen,dBlue) );


	fragColor = vec4( col, 1.0 );
}
