float sdCircle(vec2 st, vec2 pos){
   return length(st - pos); 
}

mat2 rotate(float angle){
    return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}

vec2 getPos(int index){
    vec2 pos[4];
    pos[0] = vec2(1.5, 1.);
    pos[1] = vec2(0.7, -1.);
    pos[2] = vec2(-0.5, 1.);
    pos[3] = vec2(-1.2, -1.);
    return pos[index];
}

vec3 getColor(int index){
    vec3 color[4];
    color[0] = vec3(0.2, 0.4, 2.0);
    color[1] = vec3(1.0, 0.2, 3.0);
    color[2] = vec3(2.0, 0.2, 0.2);
    color[3] = vec3(1.0, 0.2, 0.5);
    return color[index];
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord.xy * 2. - iResolution.xy) / iResolution.y; 
    vec3 col = vec3(0.);

    for (float i = 0.; i < 4.; i=i+1.){
        vec2 translate = getPos(int(i));        
        float timerSign = 1.;

        for (float j = 0.5; j <= 1.0; j+=0.1) {
            vec2 st0 = st + translate;
            timerSign *= -1.;
            float circle = sdCircle(st0, vec2(0.));
            circle -= j;

            circle = abs(circle);    
            circle = 0.009 / circle;

            vec3 sphere = vec3(circle);
            st0 *= rotate(iTime * j * 0.25 * timerSign);             
            sphere *= getColor(int(i)) * st0.x;

            col = max(col+sphere, col);        
        }
    }

    fragColor = vec4(col,1.0);
}
