uvec4 r(uvec4 e) { return 94587u * e ^ e.wxyz; }

vec4 f(vec2 x) { return vec4(r(r(r(uvec4(x + x, x))))) / vec4(-1u); }

vec4 s(vec4 c, vec2 v, float y) {
    return (y - 1.0) * c / y + 0.5 / y * (f(floor(v / y)) + f(ceil(v / y)));
}

void mainImage(out vec4 c, vec2 v) { // Dirt Texture.
    c -= c; v += 2e1 * iTime;        // By Rik Riesmeijer, 2024 - No rights reserved. 
    c = s(c, v, 1.0); c = s(c, v, 2.0); c = s(c, v, 4.0);
    c = s(c, v, 8.0); c = s(c, v, 16.0); c = s(c, v, 32.0);
    c = vec4(mix(vec4(0.122,0.075,0.000,1),
                 vec4(1.000,0.851,0.439,1),
                 smoothstep(0.0, 2.0, length(c))));
}
