// Lost keys (procedural grass)
// By Rik Riesmeijer, 2024 - No Rights reserved.
void mainImage(out vec4 c, vec2 v) {
    ivec2 j, i = ivec2(fract(fract(v) - 0.8) * v * 1.5 + v
                          + vec2(3e1 * pow(cos(iTime), 4.),
                          2e2 * iTime - 2e2 * sin(iTime)));
    c = vec2(0, 1).yxyy;
    for(; j.x++ < 11; c.zw += c.xy) {
        if(0 == (i /= 2).x % 2)
            c = c.zwxy;
        if(i.y % 2 == 1)
            c = c.zxyw;
    }
    float r = fract(0.5 + fract(-0.5 * (c.x + c.w - 
                               sqrt(abs(c.x * c.x + c.w * c.w 
                                - 2.0 * c.x * c.w - c.z * c.y)))));
    c = vec4(fract(r / 0.7), fract(r - 0.6), fract(-r) * r, 1);
}
