Main {

    Q = B(U)/float(iFrame);
    if (A(U).x==0.)Q += .2*sin(0.+2.*U.y/R.y+U.x/R.x*vec4(1,2,3,4));
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define K -36.
float map (vec3 p) {
    float d = length(p)-1.;
    p.y -= .1;
    p.yz *= ei(-.5 );
    for (float i = 0.; i < 10.; i++) {

        p.xz *= ei(.5);
        d = max(d,(abs(p.x+.2*p.z)-.8));
        d = max(d,(abs(p.y-.5*p.z)-.6));
        d = max(d,(abs(p.x*.4+p.z)-.6));
        d = max(d,(abs(p.y-.7*p.z)-.5));

    }

    return .8*(d-.1);
}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-4,0);
    float c = map(p);
    return normalize(vec3(
        map(p+e.xyy)-c,
        map(p+e.yxy)-c,
        map(p+e.yyx)-c
    ));
}
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define pi 3.14159265359

Main {
    if (iFrame > 1) { Q = A(U); return;}
     vec3 p = vec3(0,0,-1);
    vec3 d = normalize(vec3(2.*(U-.5*R)/R.y,1));
    d.xz *= ei(.1*K);
    d.yz *= ei(.1*K);
    p.xz *= ei(.1*K);
    p.yz *= ei(.1*K);
    Q = vec4(1e9);

    for (float i = 0.;i < 150.; i++) {
        float m = map(p);
        p += d*m;

        if (m < 1e-6) {Q.xyz = p;break;}
        if (m > 20.) break;
    }

}

Main {
    Q = B(U);
    vec3 p = A(U).xyz;

    vec3 o = normalize(vec3(2.*(U-.5*R)/R.y,1));
    o.xz *= ei(.1*K);
    o.yz *= ei(.1*K);
    vec3 light = vec3(-4,2,-5);
    vec4 h = hash44(vec4(U,iTime,iFrame));
    float a = h.x*2.*pi;
    float b = h.y*pi;
    vec3 n = normal(p);
    vec3 d = o;
    vec3 g = vec3(vec2(cos(a),sin(a))*sin(b),cos(b));
    d += .001*g;
    float T = 1.;
    if (map(p) > 1.) {
        p = vec3(2.*(U.xy-.5*R)/R.y,1);
        d = g;
        d.z = -abs(d.z);
        T = 5.;
    }
    d = normalize(d);
    for (float i = 0.; i < 150.; i++){
        float n = map(p);
        float m = min(max(abs(n),.00005),length(p-light)-.5);
        if (abs(n)<.0001) {
            vec4 h = hash44(vec4(U,round(i/4.),iFrame));
            vec3 no = normal(p);
            if (h.x < .7) {
                d = reflect(d,no);
                p += .005*d;
             } 
             else {
                 d = normalize(d-.5*no);
                 p += .004*d;
             }
        } p += d*abs(m);
        if (m > 100.) break;

    }

    Q.xyz += T*10.*(exp(-.03*max(length(p-light)-.5,0.)))*max(sin(-2.+3.*d.y+vec3(1,2,3)),0.);

}
