#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec4 hash(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
float box( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

#define light vec3(.1,0,-.35)
#define light2 vec3(-0.15,0,-.8)
#define light3 vec3(0.5,.2,-.5)
#define light4 vec3(0.3,-.1,-.5)
float map (vec3 p) {
    float d = length(p-light)-.05;
    d = min(length(p-light2)-.1,d);
    d = min(length(p-light3)-.1,d);
    d = min(length(p-light4)-.05,d);
    p.xy += .2;
    p.xz *= ei(.4);
    for (float i = 0.; i < 14.; i++) {
        p = abs(p)-.15*exp2(-.5*i);
        p.xy*=ei(-.2);
        p.xz*=ei(-1.5);
        p.yz*=ei(.2);
        d = min(d,length(p)-.00001);
    }
    return d;

}
vec3 normal(vec3 p) {
    vec2 e = vec2(1e-3,0);
    float m = map(p);
    return normalize(vec3(
        map(p+e.xyy)-m,
        map(p+e.yxy)-m,
        map(p+e.yyx)-m
    ));
}

Main {
    vec4 h = hash(vec4(U,iTime,1));
    Q = A(U);
    vec3 p = vec3(0,0,-1.2);
    vec3 d = normalize(vec3(2.*(U+h.xy-.5*R)/R.y,4.));

    for (float i = 0.; i < 100.; i++) {
        float m = map(p);
        p += d*max(abs(m),.001);
        if (m>10.)break;
        if (length(p-light)<.06) {Q += 3.*vec4(3,2,1,4);break;}
        if (length(p-light2)<.02) {Q += 35.*vec4(1,2,3,4);break;}
        if (length(p-light3)<.08) {Q += 18.*vec4(1);break;}
        if (length(p-light4)<.02) {Q += 37.*vec4(3,1,1,4);break;}
        if (abs(m) < .001) {
            vec3 n = normal(p);
            vec3 h = hash(vec4(p.xy*100.,i,iFrame)).xyz*2.-1.;
            //d = normalize();
            //if (dot(d,n)<0.) d = -d;
            if (h.x<-.5) d = reflect(d,n);
            else d = normalize(d-.2*n);
            p += .002*d;
        }


    }

}
