vec2 par(float t)
{
    return vec2(sin(t), cos(t) + 0.2 * sin(2. * iTime)) * (exp(cos(t + 0.2 * sin(iTime))) - 2. * cos(4. * t) - pow(sin(t / 12.), 5.));
}

void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = 5. * (2. * I - iResolution.xy) / iResolution.y;
    uv.x = abs(uv.x);
    float val = 0.;
    for(float t = 0.; t < 37.6991118431; t += 0.1)
    {
        val += 0.003 / distance(uv, par(t));
    }
    val = exp(val) - 1.1;
    vec3 col = vec3(val * 0.6, val * 0.3, val * val);
    O = vec4(col,1.0);
}
