void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=4.,e,t=iTime; vec3 p,q=vec3(4,6,2),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*e;
    p.z-=3.;
    p*=rotate3D(t*.3,q);

    for(j=0.;j++<8.;)
        p=abs(p*rotate3D(1.41+i,q))-.1,
        d=max(max(p.z,p.y),p.x),
        d-=dot(p,p)/1e4;

    e+=d;

}o+=vec4(sqrt(p/d),1)/i;
}
