#define TOTAL 100.
vec2 N22(vec2 p){
    vec3 a = fract(p.xyx*vec3(863.245, 193.35, 219.731));
    a += dot(a, a+71.47);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.xy;

    vec3 col = vec3(0);

    float minD = 100.;
    float cid = 0.;
    uv*= 3.;
    float m = 0.;     
    float t = iTime*.3+100.; 
    uv.x = -abs(uv.x);
    uv.y+=1.;
    uv.y+=.5*uv.y;
    vec2 uv0 = uv;
    uv.x *= abs(2.*uv.x/uv.y);
    uv.y *= abs(.5*uv.y/uv0.x);

    if(uv.y>0.) uv.y*=.2;
    for(float i = 0.; i < TOTAL; i++){
        vec2 n = N22(vec2(i*64.25, i*19.99))+cos(uv.x)*.05;
        vec2 p = cos(n*t);
        float d = length(uv-p);

        m += smoothstep(.02, .01, d);

        if(d<minD){
            minD = d;
            cid = i;
        }

    }


    vec3 base = sin(t*vec3(5., 4., 6.)+5.)*.5+.8;
    col -= minD*base*2.;
    col += cid/TOTAL+.4;
    //col = vec3(N22(uv).y);
    fragColor = vec4(col,1.0);
}
