// https://iquilezles.org/articles/palettes/
vec3 createPalette(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d)
{
    return a + b * cos(6.28318 * (c * t + d));
}

// https://iquilezles.org/articles/distfunctions2d/
float sdCircle(vec2 p, float r)
{
    return length(p) - r;
}

vec2 calcUv(in vec2 value)
{
    vec2 result = (value / iResolution.xy - .5) * 2.;
    result.x *= iResolution.x / iResolution.y;
    return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = calcUv(fragCoord);
    vec2 mouse = calcUv(iMouse.xy);

    float d = length(uv - mouse);

    vec3 palette = createPalette(
        d + cos(iTime) / 2.,
        vec3(.5, .5, .5),
        vec3(.5, .5, .5),
        vec3(1., 1., .5),
        vec3(.8, .9, .3)
    );

    float circle = sdCircle(uv - mouse, .5);

    float value = sin(circle * 15. + iTime);
    value = 0.25 / abs(value);

    fragColor = vec4(value * palette, 1.);
}
