// Fake/approximated soft shadow for a capsule.


// Other capsule functions:
//
// Capsule intersection: https://www.shadertoy.com/view/Xt3SzX
// Capsule bounding box: https://www.shadertoy.com/view/3s2SRV
// Capsule distance:     https://www.shadertoy.com/view/Xds3zN
// Capsule occlusion:    https://www.shadertoy.com/view/llGyzG

// Other soft-shadow functions:
//
// Sphere:    https://www.shadertoy.com/view/4d2XWV
// Ellipsoid: https://www.shadertoy.com/view/llsSzn
// Box:       https://www.shadertoy.com/view/WslGz4
// Capsule:   https://www.shadertoy.com/view/MlGczG

#define AA 3

//================================================================
// intersect capsule
float capIntersect( in vec3 ro, in vec3 rd, in vec3 pa, in vec3 pb, in float r )
{
    vec3  ba = pb - pa;
    vec3  oa = ro - pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoa = dot(ba,oa);
    float rdoa = dot(rd,oa);
    float oaoa = dot(oa,oa);

    float a = baba      - bard*bard;
    float b = baba*rdoa - baoa*bard;
    float c = baba*oaoa - baoa*baoa - r*r*baba;
    float h = b*b - a*c;
    if( h>=0.0 )
    {
        float t = (-b-sqrt(h))/a;

        float y = baoa + t*bard;

        // body
        if( y>0.0 && y<baba ) return t;

        // caps
        vec3 oc = (y<=0.0) ? oa : ro - pb;
        b = dot(rd,oc);
        c = dot(oc,oc) - r*r;
        h = b*b - c;
        if( h>0.0 )
        {
            return -b - sqrt(h);
        }
    }
    return -1.0;
}

// fake occlusion
float capOcclusion( in vec3 p, in vec3 n, in vec3 a, in vec3 b, in float r )
{
    vec3  ba = b - a, pa = p - a;
    float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
    vec3  d = pa - h*ba;
    float l = length(d);
    float o = max(0.0,dot(-d,n))*r*r/(l*l*l);
    return o;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

float getSmoothAttenuationFactor(float distSquare, float range)
{
    if (range <= 0.0f)
    {
        return 1.0f;
    }

    float factor = distSquare / (range * range);
    float smoothFactor = clamp(1.0 - (factor * factor), 0.0, 1.0);
    smoothFactor *= smoothFactor;
    return smoothFactor;
}

float getAOAttenuation(vec3 p, vec3 a, vec3 b, float r)
{
    float distSquare = sdCapsule(p, a, b, r);
    const float range = 0.5;
    return getSmoothAttenuationFactor(distSquare, range);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.7 * iTime;
	vec3 ro = vec3( 1.0*cos(an), 0.4, 1.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );


    vec3 tot = vec3(0.0);
#if AA>1
	#define ZERO min(iFrame,0)
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // camera matrix
        vec3 ww = normalize( ta - ro );
        vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
        vec3 vv = normalize( cross(uu,ww));
        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        vec3  capA = vec3(-0.3,0.0 + 0.2 * cos(3.0 * iTime),0.1);
        vec3  capB = vec3(0.3,0.2 + 0.3 * sin(3.0 * iTime),0.4);
        const float capR = 0.15;

        vec3 col = vec3(0.1, 0.2, 0.6);

        const vec3 lig = normalize(vec3(-0.8,0.8,0.2));

        float tmin = 1e20;
        float sha = 1.0;
        float occ = 1.0;
        vec3 nor;

        // plane (floor)
        {
            float t = (-0.3-ro.y)/rd.y;
            if( t>0.0 && t<tmin )
            {
                tmin = t;
                vec3 pos = ro + t*rd;
                nor = vec3(0.0,1.0,0.0);
                float ao = capOcclusion( pos, nor, capA, capB, capR );
                ao = ao * getAOAttenuation(pos, capA, capB, capR);
                occ = 1.0 - ao;
                occ = occ * occ;
            }
        }

        // capsule
        {
            float t = capIntersect( ro, rd, capA, capB, capR );
            if( t>0.0 && t<tmin )
            {
                occ = 0.0; //0.5 + 0.5*nor.y;
                col = vec3(0.0);
            }
        }

        // lighting
        if( tmin<1e19 )
        {
            col =  vec3(1.0,1.0,1.0)*occ;
        }

        tot += sqrt( col );
#if AA>1
    }
    tot /= float(AA*AA);
#endif

	fragColor = vec4( tot, 1.0 );
}
