void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5; // center

    // Make the aspect ratio correction
    uv.x *= iResolution.x / iResolution.y;

    // Calculate the distance from the center
    float dist = length(uv) / .5;

    vec3 centerColor = vec3(1.0, 1.0, 1.0); // White color at the center
    vec3 edgeColor = vec3(0.0, 0.0, 0.0); // Black color at the edges
    vec3 color = mix(centerColor, edgeColor, smoothstep(0.2, 0.8, dist));

    fragColor = vec4(color, 1.0);
}
