#define INF 9999999.
//cite inigo guy

vec3 rotateVector(vec3 v, vec3 axis, float angle) {
    return v * cos(angle) +
           cross(axis, v) * sin(angle) +
           axis * dot(axis, v) * (1.0 - cos(angle));
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}


float sdCross(in vec3 p)
{
    float da = sdBox(p.xyz,vec3(INF,1.0,1.0));
    float db = sdBox(p.yzx,vec3(1.0,INF,1.0));
    float dc = sdBox(p.zxy,vec3(1.0,1.0,INF)); 
    return min(da,min(db,dc));
}


float sdOctahedron( vec3 p, float s )
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float map(in vec3 p)
{

    float box = sdBox(p/ iTime , vec3(1.));
    float un = box;
    float s = 1. * iTime * 0.2;

    for( int m=0; m<5; m++ )
    {
        vec3 a = mod(p * s, 2.) - 1.; //clamps all scaled coords between -1 and 1
        s *= 3.; //increment scale;
        vec3 r = (1. - 3. * abs(a)); //proportional scale for cross     
        float c = sdCross(r)/s;
        un = max(un, c);



    }

    float disp = sin(5. * p.x) * sin(5. * p.y) * sin(5.0 * p.z) * 0.25;

    return un;
}

//https://michaelwalczyk.com/blog-ray-marching.html
vec3 calcNormal(in vec3 p)
{
    const vec3 small_step = vec3(0.001, 0.0, 0.0);

    float gradient_x = map(p + small_step.xyy) - map(p - small_step.xyy);
    float gradient_y = map(p + small_step.yxy) - map(p - small_step.yxy);
    float gradient_z = map(p + small_step.yyx) - map(p - small_step.yyx);

    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);

    return normalize(normal);
}


mat2 rot2D(float angle)
{
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

vec3 intersect(vec3 ro, vec3 rd)
{
    float t = 0.;
    //Raymarching
    //current position along ray
    for(float i = 0.; i < 200.; i++)
    {
        vec3 p = ro + rd * t;   //marches ray: ray origin + the direction
        float d = map(p);   //get distance to closest point from p
        t += d; //assigns distance travelled to total distance


        if(d < 0.00001f) //minimum hit distance
        {
            vec3 normal = calcNormal(p);
            return normal * 0.5 + 0.5;

            break;   //stop if ray gets too close to object
        }

        if(t > 1000.)  //stop if ray goes too far
        {
            break;
        }
    }

    return vec3(0.);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = sin(iTime) * 0.2;
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2 m = (iMouse.xy * 2. - iResolution.xy) / iResolution.y;

    vec4 col;

    vec3 ro = vec3(0, 0, -8);
    float fov = 0.5;
    vec3 rd = normalize(vec3(uv * fov, 1)); 
    float t = 0.;

   //vertical cam rotation
    ro.yz *= rot2D(-m.y);
    rd.yz *= rot2D(-m.y);

    //horizontal cam rotation
    ro.xz *= rot2D(-m.x);
    rd.xz *= rot2D(-m.x);

    vec3 rayFinal = intersect(ro, rd); 
    col = vec4(rayFinal, 1.0);
    fragColor = col;

}
