void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    //uv = uv * (vec2(pow(abs(cos(iTime / 12.0)),4.0)));
    uv.x *= iResolution.x / iResolution.y;

    int iterations = 800;
    int i = 0;
    vec2 z = vec2(0.0);


    for (i; i < iterations; i++)
    {   
        // Define the complex constant c
        vec2 c = vec2(uv.x * pow(abs(cos(iTime / 10.0)),4.0) -.7449885 , uv.y * pow(abs(cos(iTime / 10.0) ), 4.0) +.1859984);

        // Mandelbrot iteration
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;

        if (dot(z, z) > 4.0) 
            break;
    }

    float n = float(i)*8.0;
    n = n + 1.0 - log(log(dot(z, z))) / log(2.0);
    float hue = float(n) / float(iterations);
    fragColor = vec4(hue, hue*0.4, hue* 0.1, 1.0);


}
