float circle (vec2 uv, vec2 position, float radius, float blur) {
    float circle = 0.0;
    float distance = length (uv - position);
    circle = smoothstep (radius, radius - blur, distance);
    return circle;
}

vec3 hueShift (in vec3 Color, in float Shift) {
    vec3 P = vec3(0.55735) * dot(vec3(0.55735), Color);
    vec3 U = Color - P;
    vec3 V = cross(vec3(0.55735), U);    

    Color = U * cos(Shift * 6.2832) + V * sin(Shift * 6.2832) + P;

    return Color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 circleColor = vec3(0.85, 0.35, 0.2);
    vec2 center = vec2(0.5, 0.5);

    // Start with a slight hue shift that fades to zero, ie no hue shift.
    //circleColor = hueShift(circleColor, min(-0.2 + iTime * 0.4, 0.0));
    circleColor = hueShift(circleColor, max(0.1 - iTime * 0.4, 0.0));

    //float r0 = 0.3;
    //float r1 = 0.29;
    float r0 = min(iTime * 0.6, 0.3) + (sin(iTime) * 0.02 - 0.02);
    float r1 = min(iTime * 0.6, 0.3 - 0.001) + (sin(iTime) * 0.02 - 0.02);
    float c0 = circle(uv, center, r0, 0.001);
    float c1 = circle(uv, center, r1, 0.22);

    float circle = c0 - c1;


    // Calculate the fog factor based on distance.
	// Todo: Use proper fog factor? 
	// https://doc.babylonjs.com/features/featuresDeepDive/materials/shaders/Fog+ShaderMat/
    //float fogFactor = clamp(1.0 - (vDistanceFromCamera / maxDistance), 0.0, 1.0);
    float fogFactor = 1.0;

    fragColor = vec4(circleColor * circle, 1.0);
    fragColor.rgb *= fogFactor;
}
