void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float radius = length(uv);
    float angle = atan(uv.y, uv.x);

    angle += iTime * 0.5;
    radius = pow(radius, 0.8) + 0.2 * sin(iTime * 0.7);

    float vortex = sin(angle * 8.0 + radius * 12.0 - iTime * 3.0) * 0.1;

    float motion = sin(radius * 10.0 - iTime) * 0.5 + vortex;
    vec3 baseColor = vec3(0.5 + 0.5 * cos(angle + motion),
                          0.5 + 0.5 * sin(radius * 3.0 - iTime),
                          0.5 + 0.5 * cos(radius * 5.0 - iTime * 2.0));


    float glow = exp(-radius * 15.0) * 0.5;
    baseColor += glow * vec3(1.0, 0.8, 0.6);


    float vignette = 1.0 - smoothstep(0.8, 1.0, radius);
    baseColor *= vignette;

    fragColor = vec4(baseColor, 1.0);
}
