#define N 4

const float PI = acos(-1.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec4 color;

    ivec2 texSize = textureSize(iChannel0, 0);

    for (int i = 0; i < N; i++) {
        float t = iTime * 0.25 + float(i) / float(N);
        int t0 = int(t);
        t -= float(t0);
        t = smoothstep(0.0, 1.0, t);

        vec3 p = mix(
            texelFetch(iChannel0, ivec2(i, t0) % texSize, 0),
            texelFetch(iChannel0, ivec2(i, t0 + 1) % texSize, 0),
            t
        ).xyz * 4.0 - 2.0;

        vec3 c = mix(
            texelFetch(iChannel0, ivec2(i + 32, t0) % texSize, 0),
            texelFetch(iChannel0, ivec2(i + 32, t0 + 1) % texSize, 0),
            t
        ).xyz * 2.0 - 1.0;

        vec3 d = p - vec3(uv, 0.0);

        color += vec4(c, 1.0) / (dot(d, d) + 0.25);
    }

    color /= color.a;
    color = tanh(color * 2.0) * 0.5 + 0.5;

    fragColor = color;
}
