#define e 2.71828

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Centered UV.
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.x;

    //Scale it up a bit, so it's more clear what we are seeing.
    uv *= 5.;

    //seperating the equation into parts because it's a pain to write on a computer.
    float e1, e2, e3, e4;
    e1 = -pow(e, uv.x) * sin(uv.y);
    e2 = pow(e, uv.x) * cos(uv.y);
    e3 = pow(e, uv.y) * sin(uv.x);
    e4 = pow(e, uv.y) * cos(uv.x);


    //equation
    //-e^x * sin(y) + e^y * sin(x)
    //---------------------------
    //e^x * cos(y) + e^y * cos(x)
    float col = (e1 + e3) / (e2 + e4);

    //equation split to (hopefully) look cool
    //      -e^x * sin(y)                   e^y * sin(x)
    //--------------------------- + --------------------------
    //e^x * cos(y) + e^y * cos(x)   e^x * cos(y) + e^y * cos(x)
    vec2 col1 = vec2(e1 / (e2 + e4), e3 / (e2 + e4));


    //switch between the two for more things to see
    if (abs(sin(iTime / 10.)) > .5) {
        fragColor = vec4(col1, 0, 0);
    }
    else {
        fragColor = vec4(col);
    }
}
