// Crossfade between ramp, trig, sine & pulse as seen in Vsynth.

#define uv (gl_FragCoord.xy/iResolution.xy)
#define time iTime
#define speed 0.25

float freq = 2.0;
float bias = 0.5; // -> 0. - 1. range
float shape = 1.5; // -> Ramp = 0. / Trig = 1. / Sin = 1.5 / Pulse = 2.

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

      bias = 1.0-bias;

  //--Palindrome Animation--
      shape = abs(mod(time*speed, 2.0)*2.0-2.0); // -> Comment or Erase.
      shape = 2.0-shape; // -> invert to get the ramp when knob its all the way left  

    float threshold = clamp(shape, 0.0, 1.0);
    float waveMixAmt = clamp(shape, 1.0, 2.0)-1.0;  

    float ramp = fract(uv.x*freq); // Ramp
    float trig = abs(ramp*2.0-1.0); // Trig

    float wave1 = smoothstep(bias-threshold, bias+threshold, trig);
    float wave2 = smoothstep(bias-threshold, bias+threshold, ramp);
    float waveMix = mix(wave1, wave2, waveMixAmt);
    vec4  finalWave = vec4(vec3(waveMix),1.0);

      fragColor = finalWave;  

  //---1D Waveform Overlay---
    float scope = step(fract(-uv.y+waveMix*0.5-0.75), 0.025); 
    vec4 waveScopeMix = finalWave-vec4(vec3(scope),1.0);
         waveScopeMix = clamp(waveScopeMix, 0.0, 1.0);
         waveScopeMix = waveScopeMix+vec4(0,scope,0,1);

    fragColor = waveScopeMix;
  //-------------------------

}
