vec3 palette (float t)
{
    vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b*cos(6.28318*(c*t+d));
}


float fractal(vec2 p) 
{
    vec2 c = vec2(0.4, 0.351)+p/(pow(1.5, sin(iTime / 10.) * 12. + 12.));
    vec2 z = vec2(0.);
    float maxIter = 200.;
    for (float i = 0.; i<maxIter; i ++) {
        z = mat2(z,-z.y,z.x) * z + c;
        if (dot(z,z) > 4.) {
            return i/maxIter;
        }
    }
    return 0.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = 4.*(fragCoord - 0.5 * iResolution.xy)/iResolution.x;
    float d = fractal(pos);
    vec3 color = d * palette(d) * 1.4;
    fragColor = vec4(color, 1.);
}
