void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);
    if (uv.x < .23 || uv.x > .77 || uv.y < .03 || uv.y > .97) {
        // background/wall
        float plank_r = hash11(floor(uv.x*12.));
        col = texture(iChannel0, rot(PI/2.1 + PI*plank_r*.1)*uv*(2.+plank_r)).rgb;
        col += .1;
        col *= .8;
        col -= smoothstep(0.96,1.,fract(uv.x*12.))/6.;
        col += smoothstep(0.04,.0,fract(uv.x*12.))/8.;
        col -= 0.5*(smoothstep(0.19,0.3,uv.x) - smoothstep(0.7,0.81,uv.x)) * smoothstep(0.,0.04,uv.y) * smoothstep(0.,.1,1.-uv.y);
        fragColor = vec4(col,1.);
        return;
    }

    col = vec3(sin(sin(uv.x*300.*sin(uv.x*3.+iTime*.3))+uv.y*300.+sin(iTime*3.+sin(iTime*10.)*.2-uv.x*50.)*10.)*.5+.5);
    col = smoothstep(0.1,0.8,col);
    // Output to screen
    fragColor = vec4(col,1.0);
}

float hash11(float p) {
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

mat2 rot(in float angle) {
    float s = sin(angle);
    float c = cos(angle);

    return mat2(
        c, -s,
        s, c
    );
}

#define PI 3.14289
